warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-3*length(texsize.zw)) + 0.5 ).xyz);
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q29
                     );
if(ret.x> q21*q13         && ret.x<= q24*q11        )ret.y -= (noise9).x*.5; 
