waves.point_eqs_eel:
//plot x,y,z to point on circle
smp=sample*6.283;
xp=sin(smp )*0.10;
yp=cos(smp )*0.10;
zp=0;


//alter shape;
angy=sin(sample*6.28*4 +t1 )*6.28;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;


//rotate on y axis;
angy=t1*0.1;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;

//rotate on x axis
axs1 = sin(t1*0.15) + 1.6;
yq= yp*cos(axs1) - zp*sin(axs1);
zq= yp*sin(axs1) + zp*cos(axs1);
yp=yq;
zp=zq;

//rotate on y axis again
axs2 = sin(t1*0.1)*3.3;
xq=xp*cos(axs2) - zp*sin(axs2);
zq=xp*sin(axs2) + zp*cos(axs2);
xp=xq;
zp=zq;

//stretch y axis to compensate for aspect ratio
yp=yp*1.2;

//push forward into viewpace
zp=zp+2.1;

//project x,y,z into screenspace
xs=xp/zp;
ys=yp/zp;

//center 0,0 in middle of screen
x=xs+0.5+q4;
y=ys+0.5+q5;

r=1-q1;
g=1-q2;
b=1-q3;

frame_eqs_eel:
decay=1;

vol= (bass+mid+treb)*0.55;
vol=vol;


mv_r = 0.5 + 0.4*sin(time*1.324);
mv_g = 0.5 + 0.4*cos(time*1.371);




zoom=.9;

musictime=musictime+vol;
q4=sin(musictime*0.02)*0.3;
q5=sin(musictime*0.01)*0.3;

dx=sin(musictime*0.1)*0.07;
dy=cos(musictime*0.069)*0.07;




monitor=rot;

warp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (uv - 0.5);
 ret_1.x = texture (sampler_main, (tmpvar_2 + 0.5)).x;
 ret_1.y = texture (sampler_main, ((tmpvar_2 * 0.985) + 0.5)).y;
 ret_1.z = texture (sampler_main, ((tmpvar_2 * 0.97) + 0.5)).z;
 vec3 tmpvar_3;
 tmpvar_3.x = -(ret_1.x);
 tmpvar_3.y = (ret_1.x - ret_1.y);
 tmpvar_3.z = (ret_1.y - ret_1.z);
 ret_1 = (ret_1 + (tmpvar_3 * 0.03));
 ret_1 = (ret_1 + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 3.0));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   (0.5 - uv)
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) * 2.0);
 ret_1 = ((ret_1 * (1.0 - ret_1)) * 4.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
