static float2 sunpos = float2 (rand_preset.x,-.1-rand_preset.y/2+vshift)*aspect.xy;
float3 lavcol (float t) {return saturate(float3(t,pow(t,2.1),pow(t,6)));}
float3 foncol (float t) {return saturate(float3(t,pow(t,2.1),pow(t,4)));}

shader_body {
float t = time*0.1; // set velocity here
float2 c = (uv-0.5)*aspect.wz;
float1 ang_hq = atan2(c.x,c.y);
float2 spiral = float2(ang_hq*M_INV_PI_2*2+t,aspect.w*log(length(c))*.6+ang_hq*M_INV_PI_2*2-t*4);
float2 mirror = 0.5 + (0.5 - abs( frac( spiral * 0.5 ) * 2.0 - 1.0 )); // mirror
uv = mirror;

uv0 = uv;
//uv = (uv-.5)*1+.5;
uvc = (uv-.5)*aspect.xy+.5;
uv_l = uv + float2(.005*time,0);

float berg1 = lum(tex2D (sampler_noise_mq,uv_l.x/4+.02*trel))/6-uv.y+.8;
float berg2 = lum(tex2D (sampler_noise_hq,uv_l.x/6))+uv.x-.5;
sun = .1/length(sin(uvc-sunpos)); //##Vorfaktor spielen

uv2 = frac(uv);
fountain = 1*tex2D(sampler_fc_main,uv2).r +2* GetBlur2(uv2).r;
fountain=foncol(fountain.r);

w = uv.y-berg2/4-vshift; //#hier spielen
z = .2/w;
skymask = saturate(-.2-8*q5*w); //32*w fuer mehr Leuchten
lavmask = saturate(-1-12*w); //lava background

rs0.x = (uv.x-.5) * (1+abs(z))*2 ; rs0.y = z; //#hier an q29 anpassen
rs0 *= 4*(z<=0)+1;

float glow = q26/length(rs0-float2(0.,1.5+w))/4;
rs0 *= 1+glow*glow;
rs.x = rs0*2; rs.y = rs0.y + trel*.5;

float bridge = tex2D (sampler_main,uv).g*skymask;

lavamod = saturate((tex2D (sampler_noise_hq,rs/16)-.5));
uv_vol = float3(rs*2,.2*trel+0*lavamod)*(z >=0); //# faster
lava = 2*saturate(tex3D (sampler_noisevol_hq,uv_vol)-.2);
lava = lava*lavamod/sqrt(abs(z))+glow;

ret1 = (uv.y*(1+q23)+sun)*skycol*skymask + lavcol(lum(lava)) * (1-lavmask);
ret1 *= saturate(1-bridge*8);

smoke = GetPixel(frac((uv-.5)*1+.5)).b; smoke *= smoke*q5;
ret1 *= saturate(1-smoke*sm_dens*skymask);
smoke2 = GetPixel((uv-.5)/2+.5).b*q30; ;

ret1 += fountain -z*saturate(smoke+w*sm_lit)*float3(1,.4,0.1)*skymask; 

ret1 += lavcol(saturate(bridge*z*z/16));
ret1 *= saturate(berg1*32);
ret1 = ret1*saturate(1-smoke2)+lavcol(smoke2)*1;
ret = 1-exp(-ret1*2); //exposure control
}
