frame_eqs_eel:
wave_r = 0.45 + 0.45*sin((time+bass)*1.133);
wave_g = 0.45 + 0.45*sin((time+bass)*1.333);
wave_b = 0.45 + 0.45*sin((time+bass)*1.533);

// Beat detection by martin!

dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.99, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;

x = if(is_beat,rand(60)*0.01 + 0.2,x);
y = if(is_beat,rand(60)*0.01 + 0.2,y);
z = if(is_beat,rand(2)-1,z);
wave_mystery = if(above(x,y),0,-1);
q1 = x;
q2 = y;
q3 = z;

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   -(((uv - 0.5) * 1.003))
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) + min (vec3(0.1, 0.1, 0.1), (_qa.xyz * 0.25)));
 ret_1 = (ret_1 - 0.1);
 ret_1 = (ret_1 * 1.5);
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*1.003*float2(-1,-1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret += min(.1,(_qa.xyz*.25)); //gamma
    ret -= .1;
    ret *= 1.5;
    //ret -= hue_shader; //old hue shader effect
