warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 1.2));
 ret_1 = (pow (ret_1, vec3(0.5, 0.7, 1.4)) * 1.25);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*1.2;
    ret = pow(ret, float3(0.5,0.7,1.4))*1.25;
}
