shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_fc_main, uv).xyz;
 ret_1 = (ret_1 - 0.002);
 if (((rad < (0.3 + 
   (0.01 * bass)
 )) && ((
   (ret_1.x + ret_1.y)
  + ret_1.z) < 0.3333))) {
   ret_1.x = (ret_1.x + (0.004 * bass));
