warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken (decay) over time
    ret *= 1.00;
    ret -= 0.002;

    if(rad < .3+(.01*bass)){
      if(ret.x + ret.y + ret.z < .3333){
        ret.x+=.004*bass;
        ret.y+=.004*treb;
        ret.z+=.004*mid;
      }
    }
}
