waves.point_eqs_str:
masterspeed=604*q1;
masterspeed=30;
rotation1=time*0.01;

tm=time*masterspeed*0.1;// + t1*0.002*masterspeed;
perspective=0.7;

//error bump
error = sin(time*128.5)*8 - 7;
error = error * above(error,0);
error=error+1;
error=1;

//plot x,y,z to point on sphere
flx=sin(tm*14);
fly=cos(tm*14);
smp=sample*6.283;
xp=sin(smp*masterspeed )*0.25 * error;
yp=cos(smp*masterspeed )*0.25 * error;
zp=0;



//rotate on y axis;
angy=time*43;
angy=q1*100;
xq=xp*cos(angy) - zp*sin(angy);
zq=xp*sin(angy) + zp*cos(angy);
xp=xq;
zp=zq;

t2=xp;
t3=yp;
t4=zp;

//rotate on x axis
axs1 = sin(time) + 1.6;
yq= yp*cos(axs1) - zp*sin(axs1);
zq= yp*sin(axs1) + zp*cos(axs1);
yp=yq;
zp=zq;

//rotate on y axis again
axs2 = sin(time*0.4)*3.3;
xq=xp*cos(axs2) - zp*sin(axs2);
zq=xp*sin(axs2) + zp*cos(axs2);
xp=xq;
zp=zq;

//stretch y axis to compensate for aspect ratio
yp=yp*1.2;

//push forward into viewpace
zp=zp+2.1;

//project x,y,z into screenspace
xs=xp/zp;
ys=yp/zp;

//center 0,0 in middle of screen
x=xs+0.5;
y=ys+0.5;

r=(value1+0.5)*0.01;
g=(value1+0.5)*0.4;
b=(value1+0.5)*0.01;

