init_eqs_eel:
db=0;
dt=0;
avgdb=bass-bass_att;
avgdt=treb-treb_att;
lbass=0;
ltreb=0;

avgbbeatrate=.5;
beatb=0;
beatb2=0;
lbbtime2=time;
rawbeatb=0;
lbbtime=time;
avgdb=.01;
ph=0;
phase=0;
bool=0;
const=.01;
mtime=100+bass_att*1000;
mtime2=100+treb_att*1000;
mtime3=100+bass_att*1000;
mtime4=100+treb_att*1000;



trot=-.9;
bc=0;
c1=0;
tx=0;
ty=0;
ccx=.5;
ccy=.5;

frame_eqs_eel:
db=(bass-lbass)*fps;
dt=(treb-ltreb)*fps;

ltreb=treb;
lbass=bass;


avgdb=avgdb*.9+abs(db)*.1;
avgdt=avgdt*.9+abs(dt)*.1;

//beatdection
rawbeatb=above((db),avgdb*2);
beatb=rawbeatb*above(time-lbbtime,avgbbeatrate*.75);
beatb2=beatb*above((dt),avgdb*1.5);
beatbhard=beatb2*((db)-avgdb*3);

//beatrate calc
//avgbbeatrate=beatb2*(avgbbeatrate*.9+(time-lbbtime2)*.1)+(1-beatb2)*(avgbbeatrate+(3-avgbbeatrate)*.001);
avgbbeatrate=beatb*(avgbbeatrate*.99+(time-lbbtime)*.01)+(1-beatb)*(avgbbeatrate+(3-avgbbeatrate)*.00001);

lbbtime2=time*beatb2+(1-beatb2)*lbbtime2;
lbbtime=time*beatb+(1-beatb)*lbbtime;

ph=(time-lbbtime2)*(60/avgbbeatrate)*const;
phase=max(min( ph, 1 ),0);
const=const*(1-beatb2)+beatb2*( const+ (.001*bool) );
bool=below(phase,.98)*(.99-ph)-above(phase,.98)*(ph*.5);

bc=bc+beatb;

mtime=mtime+(abs(dt)*.001+beatbhard*beatb2)*(1/fps);
mtime2=mtime2+(abs(db)*.001+beatbhard*beatb2)*(1/fps);
mtime3=mtime3+avgdt*.1*(1/fps);
mtime4=mtime4+avgdb*.1*(1/fps);

warp=0;


decay=1;
ob_a =.9+phase*.1;
//mv_a = if(beat,1,0.05);
//invert=above(sin(time*200),0);


q1=rawbeatb;
q2=beatbhard;
q3=phase;
q4=60/avgbbeatrate;
q5=mtime-mtime4;
q6=mtime3-mtime2;


mv_r=sin(mtime2)*.5+.5;
mv_g=sin(mtime4)*.4+.4;
mv_b=sin(mtime3)*.5+.5;
monitor=q6;

c1=equal(bc%8,0);
tx=tx*(1-c1)+c1*mtime*10;
ty=ty*(1-c1)+c1*mtime2*10;

trot=trot*(1-c1)+c1*(sin((mtime3+mtime4))+2);
rot=trot;

ccx=.5+sin(tx*.1)*.4;
ccy=.5+sin(ty*.1)*.4;
q7=ccx;
q8=ccy;
cx=ccx;
cy=ccy;

