warp_hlsl:
shader_body
{
  float2 uv2 = lerp(uv_orig, uv, 0.2);

  // diffused version:
  float ss = 0.3;
  float3 blur = 0.25*(
    tex2D(sampler_main, uv2+ss*texsize.zw*float2( 1, 1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2(-1, 1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2( 1,-1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2(-1,-1)).xyz
  );

  // diffusion:  
  ret = tex2D(sampler_main,uv2);//blur;

  float F2 = 0.035;
  float k2 = 0.060;
  float s = 0.025;
  float scale = 9;

  // reaction:
  float3 z = ret;
  float uvv = ret.x*ret.y*ret.y;
  z.x += (-uvv + F2*(1-ret.x) + 0.0007)*scale;
  z.y += (uvv - (F2+k2)*ret.y)*scale;
  //z.x += s*(16-ret.x*ret.y);
  //z.y += s*(ret.x*ret.y - ret.y - ret.z);
  z.z -= 0.02;

  // apply laplacian:
  z.x += (blur - GetPixel(uv2))*0.2*scale;

  ret = z;  
  //ret *= 0.98 + 0.02*saturate( (0.75 - rad )*10 );
  //ret *= 0.99;

  // add noise:
  ret.x += 0.09*(tex2d(sampler_noise_lq, uv*texsize.xy*texsize_noise_lq.zw + rand_frame.xy).xyz*2-1);

  // darken (decay) over time
  //ret = (ret - 0.002)*0.99;
}

comp_hlsl:
sampler sampler_seaweed;

shader_body
{
    float lum = tex2D(sampler_main, uv).z;

      // note the use of the _pw suffix here; the 'p' stands
      // for 'point sampling' - it turns off
      // bilinear interpolation ('f'), giving is "blocky" pixels.
      // (the 'w' is for wrap - alternative is 'c' for clamp.)
      lum = abs(lum - tex2D(sampler_pw_main, (uv-0.5)*0.08 + 0.5).z);

    ret = lum + 0.13*cos(lum*29+2.8);

    float2 texc = float2(slow_roam_cos.x, slow_roam_sin.y);
    ret = pow(lum,0.5) * (1-tex2D(sampler_seaweed, texc + float2(0.1,0)*lum));

    ret *= 2; // a little bit of overbright
}
