warp_hlsl:
shader_body
{
    float2 d = texsize.zw * 8;
    float3 dx = ( GetBlur1(uv + float2(1,0)*d) - GetBlur1(uv-float2(1,0)*d) );
    float3 dy = ( GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv-float2(0,1)*d) );

    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.24 + rand_frame.xy;

    float2 my_uv =  lerp(uv_orig,uv,1) ;
    float2 uv_x= my_uv + float2(dx.x,dy.x)*texsize.zw  *4;
    float2 uv_y= my_uv + float2(dx.y,dy.y)*texsize.zw  *4;
    float2 uv_z= my_uv + float2(dx.z,dy.z)*texsize.zw  *4;
    ret.x = GetPixel(uv_x).x - (GetPixel(uv_x) - GetBlur3(uv_x)).x  * 0.02;
    ret.y = GetPixel(uv_y).y - (GetPixel(uv_y) - GetBlur3(uv_y)).y  * 0.02;
    ret.z = GetPixel(uv_z).z - (GetPixel(uv_z) - GetBlur3(uv_z)).z  * 0.02;

    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5) *0.0;
