band3min = if( below(section,zerotest) , 1 , band3min);
maxhold3 = if( below(sample ,1/512) , 0 , maxhold3 );

//accumulate values
band3max = max(wave,band3max);
band3min = min(wave,band3min);

//center max and min around 0 and return max value [range 0-1]
band3temp = if( above(section,onetest), band3max - ((band3min+band3max)*0.5) , band3temp );

