box =0.5+0.8*(2*x%4+2*y%2);
q1 = 8.05+0.3*(sin(pow(x,3)+0.137*time)-cos(pow(y,3)+0.213*time));
zoom = if(above(box,1),q1*.1,zoom);
rot = if(above(box,1),0.5*sin(0.5*rad+0.385*time),rot);
cx = cx - 0.21*sin(rad+2*q4);
