warp:
uniform sampler2D sampler_rand00_smalltiled;
 shader_body { 
  float tmpvar_1;
  tmpvar_1 = fract((time * 0.5));
  float tmpvar_2;
  tmpvar_2 = pow (0.5, tmpvar_1);
  vec2 tmpvar_3;
  tmpvar_3 = (uv - 0.5);
  vec4 tmpvar_4;
  tmpvar_4.w = 1.0;
  tmpvar_4.xyz = (mix (texture (sampler_fc_main, uv).xyz, (
    mix (texture (sampler_rand00_smalltiled, (((
      (tmpvar_3 * tmpvar_2)
     + 0.5) * texsize.xy) * texsize_rand00_smalltiled.zw)).xyz, texture (sampler_rand00_smalltiled, (((
      (tmpvar_3 * (tmpvar_2 * 2.0))
     + 0.5) * texsize.xy) * texsize_rand00_smalltiled.zw)).xyz, vec3(tmpvar_1))
   * 
    (clamp ((rad * 1.3), 0.0, 1.0) * 1.1)
  ), vec3(0.15, 0.15, 0.15)) * 0.8);
  ret = tmpvar_4.xyz;
 }

warp_hlsl:
sampler sampler_rand00_smalltiled;
float4 texsize_rand00_smalltiled;

shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // this creates an endless texture zoom:
    {  // the { } block creates a local, temporary scope for any new variables
     float t = frac(time*0.5);
     float zoom1 = pow(0.5, t);
     float zoom2 = zoom1*2;
     float2 uv1 = (uv-0.5) * zoom1 + 0.5;
     float2 uv2 = (uv-0.5) * zoom2 + 0.5;    
     float3 c  = tex2D(sampler_rand00_smalltiled, 
                  uv1*texsize.xy*texsize_rand00_smalltiled.zw).xyz;
     float3 c2 = tex2D(sampler_rand00_smalltiled, 
                  uv2*texsize.xy*texsize_rand00_smalltiled.zw).xyz;
     c = lerp(c, c2, t);
     c *= saturate(rad*1.3)*1.1; //darken @ center
     ret = lerp(ret, c, 0.15);
    }

    // darken over time
    ret *= 0.80; //or try: ret -= 0.004;

}
