avgdb=avgdb*.99+abs(db)*.01;

dt=(treb-ltreb)*fps*.06;
ltreb=treb;
avgdt=avgdt*.99+abs(dt)*.01;


pitch=(y*cos(avgdt)+cos(avgdb))*.5+.5;

avgvol=avgvol*.99+(avgdb+avgdt)*.005;


monitor=pitch;

fc=(30/fps)*avgvol*.01;
bb=above(abs(db),avgdb*6);
bt=above(abs(dt),avgdt*6);

t1=t1+(avgdb*.01*(1.5-pitch)+bb*db)*fc;
t2=t2+(avgdt*.01*pitch+bt*dt)*fc;
t3=t3+abs(db)*.1*avgvol*fc;
t4=t4+abs(dt)*.1*-avgvol*fc;



