pixel_eqs_eel:
//use simple fuzzy logic to model zooming

//rules
fuzzy1 = pow(x,2)*2;
fuzzy3 = -pow((x-1),2)*2;
fuzzy2 = pow(y,2)*2;
fuzzy4 = -pow((y-1),2)*2;


//activations approx sawtooth with sin curve
f1 = sin(q5 - 1.5707);
f2 = sin(q5);
f3 = sin(q5 + 1.5707);
f4 = sin(q5 + 3.14);

//take only values above zero
f1 = f1*above(f1,0);
f2 = f2*above(f2,0);
f3 = f3*above(f3,0);
f4 = f4*above(f4,0);

//sum rule ativations
sum_fuzzy = fuzzy1*f1 + fuzzy2*f2 - fuzzy3*f3 - fuzzy4*f4;

zoom = 0.98 + 0.2*sin(q1*0.1) + 0.1*rad  - sum_fuzzy*0.4*rad;
rot = sum_fuzzy*0.2*rad;
