waves.point_eqs_str:
n=sample*6.283;
phs=-sample * 0.2;
tm=time*0.4 + phs;

flip=flip+1;
flip=flip*below(flip,2);

xp=0;
yp=flip*0.1 + (sin(tm)*0.5 + 0.5)*0.2;
zp=0;

//wrist movement;
ang=sin(tm*2)*0.5 +0.5;

xq=xp;
yq=yp*sin(ang) + zp*cos(ang);
zq=yp*cos(ang) - zp*sin(ang);
yq=yp;
zq=zp;

ang=tm*8;
xp=xq*sin(ang) + yq*cos(ang);
yp=xq*cos(ang) - yq*sin(ang);
zp=zq;

//forearm movement;
zp=zp-0.3;
ang=3.14 + sin(tm*2 - 0.5)*1.5;
xq=xp;
yq=yp*sin(ang) + zp*cos(ang);
zq=yp*cos(ang) - zp*sin(ang);

//upper arm twist
ang=-1.0 + cos(tm*3 + 0.5);
xp=xq*sin(ang) + yq*cos(ang);
yp=xq*cos(ang) - yq*sin(ang);
zp=zq;

//upper arm outward;
zp=zp-0.35;
ang=cos(tm*2)*0.75 - 1.05;
xq=xp*sin(ang) + zp*cos(ang);
yq=yp;
zq=xp*cos(ang) - zp*sin(ang);

//upper arm up down;
ang=cos(tm)*0.5 - 0.5;
xp=xq;
yp=yq*cos(ang) - zq*sin(ang);
zp=yq*sin(ang) + zq*cos(ang);

//xp=xq;yp=yq;zp=zq;


//project into screenspace and draw on screen
zp=zp+2;
xs=xp/zp;
ys=yp/zp;

x=xs+0.5;
y=ys*1.3+0.5;


//a=(1-sample)*flip;

//b=b+pow(1-sample,2)*0.3;

x = 0.5 + (x-0.5)/q1;
y = 0.5 + (y-0.5)/q2 + 0.1;

