frame_eqs_eel:
q1 = aspectX; // you need them in your custom waves
q2 = aspectY;

w = time*0.1;   // ang
q3 = sin(w);
q4 = cos(w);
q5 = 0.5;  // x
q6 = 0.5;  // y
q7 = 1.1;  // 1/texzoom
q8 = 0.5;  // rotation centre x
q9 = 0.5;  // rotcy

wave_a = 0;

warp:
shader_body { 
 vec2 uv_r_1;
 vec2 tmpvar_2;
 tmpvar_2.x = q8;
 tmpvar_2.y = (1.0 - q9);
 vec2 tmpvar_3;
 tmpvar_3 = ((uv_orig - tmpvar_2) * aspect.xy);
 vec2 tmpvar_4;
 tmpvar_4.x = ((q4 * tmpvar_3.x) - (q3 * tmpvar_3.y));
 tmpvar_4.y = ((q3 * tmpvar_3.x) + (q4 * tmpvar_3.y));
 uv_r_1 = (q7 * tmpvar_4);
 vec2 tmpvar_5;
 tmpvar_5.x = q5;
 tmpvar_5.y = q6;
 uv_r_1 = ((1.0 - tmpvar_5) + (uv_r_1 * aspect.zw));
 vec4 tmpvar_6;
 tmpvar_6.w = 1.0;
 tmpvar_6.xyz = (texture (sampler_fc_main, uv_r_1) - 0.004).xyz;
 ret = tmpvar_6.xyz;
}

warp_hlsl:
float1 s,c,m;

shader_body
{
float2 uv_r = (uv_orig-float2(q8,1-q9))*aspect.xy;
s = q3;
c = q4;
uv_r = q7*float2( c*uv_r.x - s*uv_r.y, s*uv_r.x + c*uv_r.y);

uv_r = 1-float2(q5,q6) + uv_r*aspect.zw;

ret = tex2D(sampler_fc_main, uv_r) - 0.004;
}
