warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, mix (uv, uv_orig, vec2(0.15, 0.15)));
 ret_1.xy = tmpvar_2.xy;
 vec2 tmpvar_3;
 tmpvar_3 = (uv + ((vec2(0.0, -20.0) * texsize.zw) * texture (sampler_main, uv).z));
 ret_1.z = (tmpvar_2.z + (texture (sampler_main, tmpvar_3).z - (0.5 * 
   ((texture (sampler_blur2, tmpvar_3).xyz * scale2) + bias2)
 .z)));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1.z = texture (sampler_main, uv).z;
 ret_1.xy = vec2(0.0, 0.0);
 ret_1.y = (0.5 * (ret_1.z * (
   (0.8 + (0.15 * treb_att))
  - uv.y)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

