warp_hlsl:
shader_body
{
    // sample previous frame, with some trails...
    float trail_decay = 0.95;
    ret = tex2D( sampler_fc_main, uv ).xyz;
    float3 s2 = tex2D( sampler_fc_main, lerp(uv,uv_orig,0.33) ).xyz*trail_decay;
    ret = max(ret, s2);

    // a color-holding posterize effect:  
    float3 m = float3(7,6,8);
    ret = lerp(ret, floor(ret*m+0.25)/m, 0.1);


    // darken over time
    float center = 1-saturate(length(uv-uv_orig)*40);
    ret = (ret*0.995 - 0.001 - center*0.002); 

}

