shapes.frame_eqs_eel:
sample=instance/num_inst;

x= cos(4.3*sample+.03*time);
y= sin(4.3*sample+.03*time);

x = 0.5 + .8*x/q6;
y = 0.5 + .8*y/q5;

///// grid 

i=instance;
ni=num_inst;

sx=-.44+(i%ni)/ni;
sy=-.33+(i%sqrt(ni))/sqrt(ni);

sx=sx-.3*sy;

my_x = sx;
my_y = sy;
my_z = 0;


/////// rotations... by flexi

d = 10; 
zoom = 10;

w1 = .03*time;
w2 = 0;
w3 = 0;

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

// perspective calculation

l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));
p = tan(asin(1) + atan2(d+z3,l));

my_x = zoom*sin(w)*p;
my_y = zoom*cos(w)*p;

x = 0.5 + my_x/q6;
y = 0.5 + my_y/q5;

