shapes.frame_eqs_eel:
sample=instance/num_inst;
r = .4*sin(sample*80);
g = .8*sin(sample*66);
b = .6*sin(sample*120);

//////  shifter's cube

it = it+1;
sz = 1;
ss = sample*6;
ox = sz*.5*pow(-1,it)*below(ss,1) + .5*pow(-1,it)*above(ss,1)*sz*below(ss,2) + above(ss,2)*.5*pow(-1,it)*sz*below(ss,3);
oy = (ss-.5)*sz*below(ss,1) + sz*.5*above(ss,1)*below(ss,2) + (.5-(ss-2))*sz*above(ss,2)*below(ss,3);
oz = -sz*.5*below(ss,1) + ((ss-1)-.5)*sz*above(ss,1)*below(ss,2) + sz*.5*above(ss,2)*below(ss,3);
ox = ox + above(ss,3)*below(ss,4)*-.5*sz + above(ss,4)*below(ss,5)*sz*(-.5+(ss-4)) + above(ss,5)*sz*.5;
oy = oy + above(ss,3)*below(ss,4)*.5*sz*pow(-1,it) + above(ss,4)*below(ss,5)*sz*-.5 + above(ss,5)*sz*(-.5+(ss-5));
oz = oz + above(ss,3)*below(ss,4)*sz*(.5-(ss-3)) + above(ss,4)*below(ss,5)*sz*.5*pow(-1,it) + above(ss,5)*sz*.5*pow(-1,it);


//////rotations .... by flexi

my_x=ox;
my_y=oy;
my_z=oz;

d = 2.3;    
zoom = 1;

w1 = q1;
w2 = q3;
w3 = q2;

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

// perspective calculation

l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
p = tan(asin(1) + atan2(d+z3,l));
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));

my_x = zoom*sin(w)*p;
my_y = zoom*cos(w)*p;


p1 = .03*sin(sample*(10+9*sin(q1)));
p2 = .03*sin(sample*(10+9*sin(q2)));

x = .5+my_x+p1;
y = .5+my_y+p2;

