shapes.frame_eqs_eel:
//// log tower

i=instance;                           sample=i/num_inst;
c1=bnot(instance%20);                 c2=bnot(instance%40);
p=int(20*sample);                     t=3*time;
rad1=rad*(1+.5*sin(.05*t));
rad=rad1*log(.05*p)*sin(.05*p+.1*t);                  rad=rad*(1-.93*c1);

///// losang
x1=sin(i+t);                          y1=cos(i+t);
x1=x1-.9*atan(x1*y1)*sign(y1);
mx=x1*sin(.1*t)+y1*cos(.1*t);         my=y1*sin(.1*t)-x1*cos(.1*t);

x=.0*sample*sin(-.15*t);              y=.5*sin(5*sample-.1*t)*rad1*sample;
x= x+8.*c1*rad*mx;                    y= y+8.*c1*rad*my;
x=.5+ x/q6;                           y=.5+ y/q5;

r=if(c1,1,1);                         r2=if(c1,.1,-.1+2*sample);
g=if(c2,.3,1);                        g2=r2;
b=g;                                  b2=g2;
a=1-bnot(i-10)-bnot(i-20);            a2=a;
border_a=.2*a;                        ang=3.14/4-.1*t; 
sides=4+30*c1;

frame_eqs_eel:
decay=.1;
wave_a=0;
zoom=1;
warp=0;

q5=aspectx;
q6=aspecty;

bt=bt+bass_att/fps;
q1=bt;

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = texture (sampler_fc_main, uv).xyz;
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz - (roam_sin.xyz * roam_cos.zxy));
 ret_1 = (ret_1 - (roam_sin.wzy * roam_cos.xyz));
 ret_1 = (ret_1 - (texture (sampler_noisevol_hq, (
   ((vec3(0.05, 0.05, 0.0) * uv.xyy) * (texsize.xyy * texsize_noisevol_hq.zww))
  + 
   (vec3(0.0, 0.0, 0.4) * time)
 )) * 0.5).xyz);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken (decay) over time
    ret *= 1.00; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
ret-=roam_sin*roam_cos.zxy;
ret-=roam_sin.wzy*roam_cos;
float4 noise2 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*0.4 
                     );
ret -= (noise2)*.5; 
    ret *= 1.00; //gamma
}
