frame_eqs_eel:
//Martins resonant beat detection - use as it is
//NOTE: uses megabuf (0) - megabuf (1600)
//output: is_beat1 and is_beat2; 2:1 or 1:2

//-------------- Start ----------------------------
dt = min (1/fps,0.1) ; //Stability !
dec_m = 1 - 4*dt; dec_s = 1 - 1.25*dt;
vol =  max(0.5,bass+mid); 
vol_ = vol_*dec_m + (1-dec_m)*vol; excite = (vol-vol_)/vol_;
index=0; ratio = log(maxbpm/minbpm); sum_res = 0.01;
loop (num_res,
  n = index*8;
  omega      =   megabuf(n) * 6.2832/60;
  megabuf (n+1) *= 1-0.25*dt; //damping
  EC_Steps = int (10*omega*dt)+1; 
  loop (EC_Steps, // Euler-Cauchy steps
    megabuf (n+1) += (excite - sqr(omega)*megabuf(n + 2)) * dt/EC_Steps;
    megabuf (n+2) += megabuf(n+1) * dt/EC_Steps);
  beatcos  = megabuf(n+1);  beatsin = megabuf(n+2)*omega; //2pi/60
  quad     = sqrt (pow(beatsin,2) + pow(beatcos,2)) ;
  megabuf (n+3) = megabuf(n+3) *dec_s +  quad* (1-dec_s);
  megabuf (n+4) = megabuf(n+3);
  stage = 2; //Addition d. Harmonischen
  loop (2,
    ind2     = int (0.5 + index + (num_res-1)/ratio*log(stage));
    if ((ind2 <= num_res),
       n2 = ind2*8;
       A2     = max(max(megabuf(n2+3),megabuf(n2-8+3)),megabuf(n2+8+3));
      megabuf(n+4) *= 1 + 2/(stage*stage)*A2/(A2+megabuf(n+3)),0);
    stage *= 2;
  );
  Hyst = 1 + min (0.25, abs (index-maxind)*0.02);
  if (megabuf(n+4) > megabuf (maxind*8+4)*Hyst, maxind = index,0);
  sum_res += megabuf (n+3);
  index += 1;
);
certain = megabuf(maxind*8+3)/(sum_res/num_res);
//Doppelten oder, falls nicht moeglich, halben beat indizieren
maxind2     = int (0.5 + maxind + (num_res-1)/ratio*log(2));
if (maxind2 > num_res, maxind2 = int (0.5 + maxind + (num_res-1)/ratio*log(0.5)),0);
sig1 = (sig1 + megabuf(maxind*8+1))/2 + excite * 0; //check ######
is_beat1  = (sig1 > 0) && (sig1_ <= 0); 
sig2 = (sig2 + megabuf(maxind2*8+1))/2 + excite * 0; //check ######
is_beat2  = (sig2 > 0) && (sig2_ <= 0); 
fastpace = if (maxind > maxind2, sig1 * sig1_ < 0, sig2 * sig2_ < 0);
sig1_ = sig1;
sig2_ = sig2;
//-------------- End ----------------------------
tog1 = sig1 > 0;
tog2 = sig2 > 0;
sbeat1 = (sbeat1 + is_beat1)/2;
sbeat2 = (sbeat2 + is_beat2)/2;
//fastpace = if (maxind > ind2, is_beat1,is_beat2);
prog1 = (prog1 + is_beat1) %512;
prog2 = (prog2 + is_beat2) %512;
prog3 = (prog3 + fastpace) %512;

maxi = min (maxind, maxind2);
//maxi = maxind; //#####???
trunner = trunner + dt*megabuf (maxi*8)/60*2*pi;
corr = if (push, 0.5*corr + 0.5*cos (trunner), corr*dec_s);
trunner -= corr * dt;
trunner_ = dec_s * trunner_ + (1-dec_s)*trunner/2;

q14 = trunner_;
q15 = cos(trunner_);
q16 = sin(trunner_);

q32 = aspecty;
//--------------------------------------------------------
change  = is_beat1 && (prog1%20 == 0);
schange = bnot (oldind == maxind) * (t1 > 10) || (t1>20);
t1 = if (schange, 0, t1+dt);
oldind = maxind;

ran1 = if (change, rand (100)/100, ran1);
ran2 = if (change, rand (100)/100, ran2);
ran3 = if (change, rand (100)/100, ran3);
ran4 = if (change, rand (100)/100, ran4);
ran1_ = ran1_ + max(min(ran1-ran1_,0.02),-0.02)*dt*16;
ran2_ = ran2_ + max(min(ran2-ran2_,0.02),-0.02)*dt*16;
ran3_ = ran3_ + max(min(ran3-ran3_,0.02),-0.02)*dt*16;
ran4_ = ran4_ + max(min(ran4-ran4_,0.1),-0.1)*dt*8;

scale = sqrt (certain/4);

scheme = if (schange, int (rand(1)), scheme) ;
p1 = p1*dec_m+(1-dec_m)*prog1;

speed = speed *dec_s + (1-dec_s)*(maxind/num_res+0.2);

ang = ang + dt * (ran4_-0.5) * speed *2;
sa = sin (ang); ca = cos(ang);
m = 0; scheme = 0;
loop (num_shapes * (vol_> 0.5),
  n = m*recsize;
  if (scheme ==0,

  // modify 3D points here only
  // ---------------------------------
     x3 = gmegabuf (1000+n);
     y3 = gmegabuf (1001+n);
     z3 = max (0.01, abs (gmegabuf (1002+n)));
     mx = (ran1_-0.5); my = (ran2_-0.5); mz = -ran3_; 
     x3 += dt*mx*speed;
     y3 += dt*my*speed;
     z3 += dt*mz*speed;

     bri = 1;
     if ((mx > 0) && (x3/z3 >  .6), z3 = rand (100)/100+0.1; x3 = -z3*0.8; bri = 0,0);
     if ((mx < 0) && (x3/z3 < -.6), z3 = rand (100)/100+0.1; x3 =  z3*0.8; bri = 0,0);
     if ((my > 0) && (y3/z3 >  .6), z3 = rand (100)/100+0.1; y3 = -z3*0.8; bri = 0,0);
     if ((my < 0) && (y3/z3 < -.6), z3 = rand (100)/100+0.1; y3 =  z3*0.8; bri = 0,0);

     zenfac = 1;
     if (z3 < 0.05,z3 = 1;
         x3 = (rand (100)/100-0.5)/zenfac;
         y3=(rand (100)/100-0.5)/zenfac; bri = 0,0);

     gmegabuf (1000+n) = x3;
     gmegabuf (1001+n) = y3;
     gmegabuf (1002+n) = z3;

  // ---------------------------------

    k1 = m/num_shapes-0.5;
    x0 = x3 / z3;
    y0 = y3 / z3;

    x = ca * x0 + sa*y0;
    y = -sa *x0 + ca*y0;
    gmegabuf (n) = x + 0.5;
    gmegabuf (n+1) = y + 0.5;
    gmegabuf (n+3) = 0.01 / z3 * q32 * bri;;
    gmegabuf (n+4) = bnot(schange) * max(min(3*z3 * (1-z3),1),-1);
  ,0); //end if scheme ==1
m += 1);



