float2 uv_polar(float2 domain, float2 center){
   float2 c = domain - center;
   float rad_hq = length(c);
   float ang_hq = atan2(c.x,c.y);
   return float2(ang_hq, rad_hq);
}

float2 uv_lens_half_sphere(float2 domain, float2 position, float radius, float refractivity){
   float2 polar = uv_polar(domain, position);
   float cone = saturate(1-polar.y/radius);
   float halfsphere = sqrt(1-pow(cone-1,2));
   float w = atan2(1-cone,halfsphere);
   float refrac_w = w-asin(sin(w)/refractivity);
   float refrac_d = 1-cone - halfsphere*tan(refrac_w);
   float2 refrac_uv =position+float2(sin(polar.x),cos(polar.x))*refrac_d*radius;
   bool mask = cone>0;
   return !mask*domain + mask*refrac_uv;
}

