warp:
shader_body { 
 vec3 rsamp_1;
 vec3 ret_2;
 vec4 tmpvar_3;
 tmpvar_3 = texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy));
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_pw_main, uv);
 ret_2 = tmpvar_4.xyz;
 vec4 tmpvar_5;
 tmpvar_5 = texture (sampler_pw_main, (uv + ((
   (tmpvar_3.xy - 0.5)
  * 12.0) * texsize.zw)));
 vec3 tmpvar_6;
 tmpvar_6 = tmpvar_5.xyz;
 rsamp_1 = tmpvar_6;
 float tmpvar_7;
 vec2 x_8;
 x_8 = (tmpvar_4.xy - uv);
 tmpvar_7 = sqrt(dot (x_8, x_8));
 float tmpvar_9;
 vec2 x_10;
 x_10 = (tmpvar_5.xy - uv);
 tmpvar_9 = sqrt(dot (x_10, x_10));
 if ((tmpvar_7 > tmpvar_9)) {
   ret_2.xy = tmpvar_6.xy;
 };
 vec4 tmpvar_11;
 tmpvar_11 = texture (sampler_pw_main, (uv + ((
   (tmpvar_3.zw - 0.5)
  * 48.0) * texsize.zw)));
 rsamp_1 = tmpvar_11.xyz;
 float tmpvar_12;
 vec2 x_13;
 x_13 = (ret_2.xy - uv);
 tmpvar_12 = sqrt(dot (x_13, x_13));
 float tmpvar_14;
 vec2 x_15;
 x_15 = (tmpvar_11.xy - uv);
 tmpvar_14 = sqrt(dot (x_15, x_15));
 if ((tmpvar_12 > tmpvar_14)) {
   ret_2.xy = rsamp_1.xy;
 };
 ret_2.z = texture (sampler_fw_main, ret_2.xy).z;
 ret_2.z = (ret_2.z - (q1 * 0.005));
 vec4 tmpvar_16;
