}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1 = texture (sampler_main, uv);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ((texture (sampler_main, (
   ((hue_shader * 2.0) + q20)
 .xy + 
   fract((_qa.xy + (tmpvar_1.xy * 0.2)))
 )) * tmpvar_1.z) * 3.0).xyz;
