shapes.frame_eqs_eel:
q1 = time/5;
q2 = time/6;
rad = 0.005+bass/100;
q5 = bass_att/70;
b2 = treb*0.7-bass/2;

loci=instance;
loci=loci+int(sin(time)*1000);
locn=num_inst;
locn=locn+int(cos(time*1.99938348)*300);
r = loci/locn;
g = abs(treb_att/5-bass);
//b = abs(treb_att/5-bass);
size = 0.3+bass_att/300;

nc = sqrt(locn);
xs=sin(6.28*(loci%(nc))/nc)*sin(3.14*int(loci/nc)/nc);
ys=cos(6.28*(loci%nc)/nc)*sin(3.14*int(loci/nc)/nc);
zs=cos(3.14*int(loci/nc)/nc);

//modulate to allow rotation
y1 = ys*cos(q1)-zs*sin(q1);
z1 = ys*sin(q1)+zs*cos(q1);
x1 = z1*sin(q2)+xs*cos(q2);
z2 = z1*cos(q2)-xs*sin(q2);
x2 = x1*cos(q3)-y1*sin(q3);
y2 = y1*cos(q3)+x1*sin(q3);

//translate shape
x3 = x2+q4;
y3 = y2;
z3 = z2+q5;

x = 0.5 + size*(x3/(1+z3*size));
y = 0.5 + size*(y3/(1+z3*size));
a=below(z3,0);

