 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.4));
 ret_1 = (ret_1 * 0.41);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

