warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv).xyz;
    ret += GetBlur1(uv)*0.4;

    // darken (decay) over time
    ret *= 0.41; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv);
    ret *= 2.70; //gamma
}
