shapes.frame_eqs_eel:
q1 = time/5;
q2 = time/6;
rad = 0.005+bass/90;
m5 = bass_att/70;
b2 = treb*0.7-bass/2;
r = instance/num_inst;
g = abs(treb_att/5-bass);
//b = abs(treb_att/5-bass);
size = 0.3+bass_att/300-(1-q5)*0.1;

nc = sqrt(num_inst);
xs=sin(6.28*(instance%nc)/nc)*sin(3.14*int(instance/nc)/nc);
ys=cos(6.28*(instance%nc)/nc)*sin(3.14*int(instance/nc)/nc);
zs=cos(3.14*int(instance/nc)/nc);

//modulate to allow rotation
y1 = ys*cos(q1)-zs*sin(q1);
z1 = ys*sin(q1)+zs*cos(q1);
x1 = z1*sin(q2)+xs*cos(q2);
z2 = z1*cos(q2)-xs*sin(q2);
x2 = x1*cos(q3)-y1*sin(q3);
y2 = y1*cos(q3)+x1*sin(q3);

//translate shape
x3 = x2+q4;
y3 = y2;
z3 = z2+m5;

x = 0.5 + size*(x3/(1+z3*size));
y = 0.5 + size*(y3/(1+z3*size));
a=below(z3,0);

frame_eqs_eel:
wave_a = 0;
vol = .1*(vol*9 + (bass_att+mid_att+treb_att)*.333333);
q1 = vol*.7;
q1 = min(q1,2);

tic = time-tin;
tin = time;
tm = tm + tic*(bass_att+mid_att+treb_att)*.333333;
q2 = tm;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz - (roam_sin.wzy * roam_cos.zxy));
 ret_1 = (ret_1 - (ret_1 * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
