}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1);
 vec2 x_3;
 x_3 = (3.0 * ret_2.yx);
 uv_1 = (uv_1 + ((
   (-0.05 * (sign(x_3) * (1.570796 - (
     sqrt((1.0 - abs(x_3)))
    * 
     (1.570796 + (abs(x_3) * (-0.2146018 + (
       abs(x_3)
      * 
       (0.08656672 + (abs(x_3) * -0.03102955))
     ))))
   ))))
  + 
   (0.05 * cos((2.0 * ret_2.zz)))
 ) - 0.025));
 ret_2 = (texture (sampler_main, uv_1).xyz + ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1));
 vec3 tmpvar_4;
 tmpvar_4 = clamp (ret_2, 0.0, 1.0);
 ret_2 = (tmpvar_4 * tmpvar_4);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_2;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
ret-=roam_sin.wzy*roam_cos.zxy;
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{

    uv = 0.05 + 0.9*uv;
    ret = GetBlur1(uv);
    uv += -.05*asin(3*ret.yx)+.05*cos(2*ret.zz)-0.025;

    ret = tex2D(sampler_main,uv);
    ret += GetBlur1(uv);

    // palettize:
 //   ret = pow(lum(ret), float3(0.3, .76, 0.87));

    // invert
    ret = saturate(ret);
    ret *= ret;
