shapes.frame_eqs_eel:
q1 = time/5;
q2 = time/6;
rad = 0.005+bass/90;
m5 = bass_att/70;
b2 = treb*0.7-bass/2;
r = instance/num_inst;
g = abs(treb_att/5-bass);
//b = abs(treb_att/5-bass);
size = 0.3+bass_att/300-(1-q5)*0.1;

nc = sqrt(num_inst);
xs=sin(6.28*(instance%nc)/nc)*sin(3.14*int(instance/nc)/nc);
ys=cos(6.28*(instance%nc)/nc)*sin(3.14*int(instance/nc)/nc);
zs=cos(3.14*int(instance/nc)/nc);

//modulate to allow rotation
y1 = ys*cos(q1)-zs*sin(q1);
z1 = ys*sin(q1)+zs*cos(q1);
x1 = z1*sin(q2)+xs*cos(q2);
z2 = z1*cos(q2)-xs*sin(q2);
x2 = x1*cos(q3)-y1*sin(q3);
y2 = y1*cos(q3)+x1*sin(q3);

//translate shape
x3 = x2+q4;
y3 = y2;
z3 = z2+m5;

x = 0.5 + size*(x3/(1+z3*size));
y = 0.5 + size*(y3/(1+z3*size));
a=below(z3,0);

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1 = (texsize.zw * 4.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (pow ((
   (abs(((
     ((texture (sampler_blur3, uv).xyz * scale3) + bias3)
    * 2.0) - texture (sampler_main, uv).xyz)) * 1.8)
  + 0.12), vec3(1.8, 1.8, 1.8)) * ((
   dot ((((
     (texture (sampler_blur2, (uv + (vec2(1.0, 0.0) * tmpvar_1))).xyz * scale2)
    + bias2) - (
     (texture (sampler_blur2, (uv - (vec2(1.0, 0.0) * tmpvar_1))).xyz * scale2)
    + bias2)) + ((
     (texture (sampler_blur2, (uv + (vec2(0.0, 1.0) * tmpvar_1))).xyz * scale2)
    + bias2) - (
     (texture (sampler_blur2, (uv - (vec2(0.0, 1.0) * tmpvar_1))).xyz * scale2)
    + bias2))), vec3(0.32, 0.49, 0.29))
  * 4.0) + 1.0));
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp_hlsl:
shader_body
{
float2 d = texsize.zw * 4;
float3 dx = ( GetBlur2(uv + float2(1,0)*d) - GetBlur2(uv - float2(1,0)*d) );
float3 dy = ( GetBlur2(uv + float2(0,1)*d) - GetBlur2(uv - float2(0,1)*d) );

ret = pow(abs(GetBlur3(uv)*2-GetPixel(uv))*1.8+0.12,1.8);
//ret = GetBlur3(uv)*GetBlur1(uv)*4;
//ret = GetPixel(uv);
ret *= lum(dx+dy)*4+1;
}
