shader_body
{
    // sample previous frame
    ret.xyz = tex2D( sampler_main, uv ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      float2 b = lerp(GetBlur2(uv),GetBlur1(uv),uv_orig.x).xz;//GetBlur2(uv);
      ret.xz += (ret.xz - b)*0.3 - (((bass+treb+mid-0.5)*.333)*.02);
      ret.xz *= 0.95;

      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xz += (tex2D(sampler_noise_lq, dither_uv).xz-0.5)/256.0 * 122 * saturate(treb_att-1);

      // desaturate over time, to keep the globs white
      ret.x = lerp(ret.x, lum(ret.x), 0.2);

      float2 pix = texsize.zw*12;
      float3 dx = (GetBlur2(uv_orig + float2(1,0)*pix) - GetBlur2(uv_orig - float2(1,0)*pix));
      float3 dy = (GetBlur2(uv_orig + float2(0,1)*pix) - GetBlur2(uv_orig - float2(0,1)*pix));
      float2 my_uv = uv_orig + float2(dx.x,dy.x)*0.05 + float2(dx.y,dy.y)*0.02 - float2(dx.z,dy.z)*0.05;

      ret.y = tex2D(sampler_main, my_uv).y - 0.04;
      ret.y += (ret.y - GetBlur1(my_uv).y*1.2 + 0.2)*0.2;
}
