if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret += saturate(exp(-4*max(struc,bdist))) * float3(glow,0,0);

//distant light
ret += bdist*bdist*col_back *q19;

//Lamp
uv3 = uv1; 
lamp = saturate(1-4*length(uv3))*struc*saturate(1-2*bdist)*1.6;
ret += (1-lum(ret))*lamp*float3(.65,.75,1) ;

ret = 1-exp((-1.6)*ret);
}

