warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    float3 ret2 = tex2D( sampler_fc_main, uv ).xyz;

    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
    ret.z = tex2D( sampler_main, (uv-0.5)*0.96+0.5 ).z;   

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
sampler sampler_smalltiled_colors3;
shader_body 
{ 
    ret = tex2D(sampler_main, uv).xyz; 
    float2 zoom = 1.6+q3;
    float2 c = float2(0.8, 0.22);    
    float2 uxy = (uv-0.5)*zoom;
    uxy = (float2(uxy.x*uxy.x - uxy.y*uxy.y, 2*uxy.x*uxy.y));
    c = float2(c.x*c.x - c.y*c.y, 2*c.x*c.y);
    c = float2(c.x*c.x - c.y*c.y, 2*c.x*c.y);
    uxy = uxy+c;    

    ret += tex2D(sampler_smalltiled_colors3, uxy).xyz - 0.004;    
    ret *= GetBlur1(uv);
}
