warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    ret = max(ret,  tex2D(sampler_main, uv + (ret.xy-0.5)*texsize.zw*5*float2(0,1)).xyz );   

    // darken (decay) over time
    ret = (ret - 0.008)*0.96;
}

