warp_hlsl:
shader_body
{
  // sample previous frame
  ret = tex2D( sampler_pw_main, uv ).xyz;

  float n;
  float2 d = texsize.zw*1;
  n  = saturate(tex2D(sampler_pw_main, uv+d*float2( 1, 1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 1, 0)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 1,-1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 0, 1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 0,-1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2(-1, 1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2(-1, 0)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2(-1,-1)).x*999);

float t = 0.4;
  if (n < 1.9)
    ret -= t;
  if (n > 3.1)
    ret -= t;
  if (ret.x < 0.1 && abs(n-3) < 0.5)
    ret += t;

