warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret *= 0.987;

    // add noise
    //float2 uv_noise = uv*texsize_noise_lq.zw*texsize.xy + rand_frame.xy;
    //ret += (tex2D(sampler_noise_lq, uv_noise)*2-1)*0.02;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;    

    ret *= 0.6;

    // LSB accentuate effect: (LSB = least significant bit)
    float3 d = float3(texsize.zw, 0)*1.0;
    float b1 = lum(GetPixel(uv + d.xz));
    float b2 = lum(GetPixel(uv - d.xz));
    float b3 = lum(GetPixel(uv + d.zy));
    float b4 = lum(GetPixel(uv - d.zy));
    float2 c = float2(b1-b2,b3-b4);
    c = frac(c*20);
    c = saturate((c-0.5)*999)*2-1;
    c = lerp(c, 0, saturate( lum(GetBlur2(uv))*30-17 ));
    ret += 0.104*c.x;
    ret += 0.104*c.y;

    ret = lerp(ret, lum(ret), -1)*2;

}
