comp:
shader_body { 
 vec3 uvd_1;
 vec2 uvb_2;
 uvd_1 = -(((texture (sampler_blur2, uv).xyz * scale2) + bias2));
 uvd_1.x = (uvd_1.x * (1.0 - texture (sampler_noise_hq, uv.xx)).x);
 uvd_1.y = (uvd_1.y * (1.0 - texture (sampler_noise_hq, uv.yy)).x);
 uvd_1.x = (uvd_1.x * q4);
 uvd_1.y = (uvd_1.y * q3);
 uvd_1.xy = (uvd_1.xy * aspect.xy);
 uvb_2.x = (0.5 - (0.25 * texture (sampler_noise_hq, vec2((uv.x + q3))))).x;
 uvb_2.y = (0.5 - (0.25 * texture (sampler_noise_hq, vec2((uv.y - q4))))).x;
 uvb_2 = (uvb_2 * aspect.xy);
 uvb_2.x = (uvb_2.x * q3);
 uvb_2.y = (uvb_2.y * q4);
 vec3 tmpvar_3;
 tmpvar_3 = (texture (sampler_noise_hq, uvb_2) + texture (sampler_noise_hq, uvb_2)).xyz;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = (tmpvar_3 * (texture (sampler_main, uvd_1.xy).xyz * (
   (32.0 * tmpvar_3)
  + 
   ((texture (sampler_blur1, tmpvar_3.xy).xyz * scale1) + bias1)
 ).x));
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= .9-.08; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
ret=1;
float sample = tex2D(sampler_main, uv);
float x = 0;
float y = 0;
float ret1 = 0;
float ret2 = 0;
float2 uvb = 0;
float3 uvd = 0;
float blum = 0;

uvd-=GetBlur2(uv);
uvd.x*=1-tex2D(sampler_noise_hq, uv.x);
uvd.y*=1-tex2D(sampler_noise_hq, uv.y);

uvd.x*=q4;
uvd.y*=q3;

uvd.xy*=aspect.xy;

uvb.x=.5-.250*tex2D(sampler_noise_hq, uv.x+q3);
uvb.y=.5-.250*tex2D(sampler_noise_hq, uv.y-q4);
uvb*=aspect.xy;

uvb.x*=q3;
uvb.y*=q4;

float3 background = (tex2D(sampler_noise_hq, uvb)+tex2D(sampler_noise_hq, uvb));
blum = 32*(background)+GetBlur1(background);

float3 detail = tex2D(sampler_main, uvd);
background*=detail*blum;

float color = float3(1,1,1);
ret*=background;
}
