sampler sampler_cells;

shader_body
{
    float3 lightPosition = float3(0,0, 0);

    float2 cords = float2(
                          uv.x + sin(10*(uv.x) - q2*2.1)*q6, 
                          uv.y + sin(10*(uv.x) - q2*2.1 )*q6);

   //perlin noise
   float3 final_noise = float3(0,0,0);
   float3 uvx = float3(cords.y  , cords.y,  cords.y*2 );  
   for(int i=0;i<4;i++)
      final_noise +=(1.0/pow(2,i))*(tex3D(sampler_noisevol_hq, uvx*pow(2,i))*2-1);

   //get color right
   ret = ((final_noise+0.15)*2).xyz*0.75;
   ret = ret*(cords.x+1); 
   ret = lum(ret)*float3(cords.y+1,cords.x*0.2,0)*2; 


    //lighting - sacles
   float normal =  normalize(float3(1 + 12*cos(q8*uv.x - time*2.1)*0.05, 1, 0));        
   float3 light_direction = normalize(float3(cords.xy, 0) - lightPosition);
   float atn = clamp(0,1,dot(normal, light_direction));

   ret *= (atn);
   ret = lum(ret)*ret*0.3;
}
