t = rand(40)*.1;
inneralpha = rand(100)*.01;
outeralpha = rand(100)*.01;
red = rand(100)*.1;
green = rand(100)*.1;
blue = rand(100)*.1;

shapes.frame_eqs_eel:
ang = ang + (time*t)+(bass);
sides = sidesin;
x = x+(bass*.01)*sin(time*(bass*5*sin(time*5)));
y = y + (treb*.01)*sin(time*(treb*5*sin(time*5)));
a = inneralpha;
a2 = outeralpha;
r2 = red;
g2 = green;
b2 = blue;
border_a = if(above(bass,1.7),1,0);
r = bass*.5;
g = treb*.5;
b = mid*.5;

rad = if(above(bass,1.7),rad*bass,rad);

waves.point_eqs_eel:
fvar = sample*512;
svar = fvar/16;
tvar = 0&svar;
newsample = tvar/3.2;


x = newsample*.1;
y = .5+value1;
r = abs(y-.5)*10;
g = -.6 + sin(sample*5+time*5);
b = bass*.3;

frame_eqs_eel:
// -------------------------------- Beat Detective v0.07 ----------------------------------
sure=if(equal(sure,0),.6,sure);
interval=if(equal(interval,0),40,interval);
lastbeat=if(equal(lastbeat,0),frame-FPS,lastbeat);
dbass=(bass-pbass)/FPS;
beat=above(dbass,.6*maxdbass)*above(frame-lastbeat,FPS/3);
sure=if(beat*below(abs(frame-(interval+lastbeat)),FPS/5),
min(.095+sure,1),beat*(sure-.095)+(1-beat)*sure*.9996);
sure=max(.5,sure);
cheat=if(above(frame,lastbeat+interval+ int(FPS/10))*above(sure,.91),1,cheat);
beat=if(cheat,1,beat);
sure=if(cheat,.95*sure,sure);
maxdbass=max(maxdbass*.999,dbass);
maxdbass=max(.012,maxdbass);
maxdbass=min(.02,maxdbass);
interval=if(beat, frame-lastbeat,interval);
lastbeat=if(beat,frame-cheat*int(FPS/10),lastbeat);
cheat=0;
pbass=bass;
// ----------------------------------------------------------------------------------------------
warp=0;
zoom=1-(FPS/85)*.002;
q8=beat;
q7=1-2*sqrt((frame-lastbeat)/interval);  // normalized phase between beats
q7=if(below(q7,-1),1,q7);
q6=60*FPS/interval; // bpm
decay=.955+.007*75/FPS;

wx=(1-beat)*wx+beat*(-5+10*rand(1000)/1000);
wx=(1-beat)*wx+beat*(wx+3*sign(wx));
wy=(1-beat)*wy+beat*(-5+10*rand(1000)/1000);
fx=(1-beat)*fx+beat*(-1+rand(5));
am=(1-beat)*am+beat*(1.5*rand(100)/100);
am=(1-beat)*am+beat*(equal(fx,-1)*(.5+am)*sign(-wx)+above(fx,-1)*am);
rotval=(1-beat)*rotval+beat*(.02*q6/140)*(-1+2*rand(100)/100);
myrot=(1-beat)*(myrot+rotval)+beat*0;
q1=wx;
q2=wy;
q3=fx;
q4=am;
q5=myrot;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz - (tmpvar_2.xyz * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 1.2));
 ret_1 = (pow (ret_1, vec3(0.5, 0.7, 1.4)) * 1.25);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*1.2;
    ret = pow(ret, float3(0.5,0.7,1.4))*1.25;
}
