l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));
p = tan(asin(1) + atan2(d+z3,l));

x1 = zoom*sin(w)*p;
y1 = zoom*cos(w)*p;

x = 0.5 + x1/q6;
y = 0.5 + y1;

rad *= .1*zoom/d;

