y1= z* ( .3*sin(.5*q2)/(.2+p) + .3*sin(6*p-time));


///// star

p0=3;   p1=p0*sample;   p2=p1%p0;   p3=2*3.14/p0;

x=p2-p1;      y=0;

t =  20*sin(.02*time+.1*q1+.1*q2);

mx=x*sin(p2*p3+t)+y*cos(p2*p3+t);
my=y*sin(p2*p3+t)-x*cos(p2*p3+t);

d=sqrt(mx*mx+my*my);
rad=rad*(1-.6*d);

zoom=.8*z;
x=.5+(x1+zoom*mx)/q6;    y=.5+y1 + zoom*my;

rad=rad*zoom*sin(3*3.14*sample);

