waves.point_eqs_eel:
n = sample*6.283;
pi = 3.1415;
pi2 = 6.283;

phs = t1 + sample*9;

bias = t5;
bias_i = bias - 1;

cc = phs / 3;
cc_int = int(cc);
cc_ramp = cc - cc_int;
cc_ad_a = (cc_ramp * bias - 1) / bias_i;
cc_ad_a = if( below(cc_ad_a,0) , 0 , cc_ad_a );

cc_ad_b = cc_ramp * bias;
cc_ad_b = if( above(cc_ad_b,1) , 1 , cc_ad_b );

cc_a = cc_ad_a + cc_int;
cc_b = (cc_ad_b + cc_int) ;

xp = t2 * above(cc_ad_a , 0);
yp = 1;
zp = 0;

ang = cc_a * 6.283 * 16;
sang = sin(ang) ; cang = cos(ang);
xq = sang*xp + cang*zp;
yq = yp;
zq = cang*xp - sang*zp;
xp=xq;yp=yq;zp=zq;

ang2 = cc_b + t4;
sang = sin(ang2) ; cang = cos(ang2);
xq = xp;
yq = sang*yp + cang*zp;
zq = cang*yp - sang*zp;
xp=xq;yp=yq;zp=zq;

ang2 = cc_b * 3.13 + t4;
sang = sin(ang2) ; cang = cos(ang2);
xq = sang*xp + cang*yp;
yq = cang*xp - sang*yp;
zq = zp;
xp=xq;yp=yq;zp=zq;

ang2 = cc_b * 1.43 + t4;
sang = sin(ang2) ; cang = cos(ang2);
xq = sang*xp + cang*zp;
yq = yp;
zq = cang*xp - sang*zp;
xp=xq;yp=yq;zp=zq;


zp = zp+3.1;
xs = xp/zp + 0.5;
ys = yp/zp * 1.333 + 0.5;

x=xs;
y=ys;

frame_eqs_eel:
wave_x = wave_x + 0.2900*( 0.60*sin(2.121*time) + 0.40*sin(1.621*time) );
wave_y = wave_y + 0.2900*( 0.60*sin(1.742*time) + 0.40*sin(2.322*time) );
wave_r = wave_r + 0.350*( 0.60*sin(0.823*time) + 0.40*sin(0.916*time) );
wave_g = wave_g + 0.350*( 0.60*sin(0.900*time) + 0.40*sin(1.023*time) );
wave_b = wave_b + 0.350*( 0.60*sin(0.808*time) + 0.40*sin(0.949*time) );
blah = 0.5/(wave_r+wave_g+wave_b);
wave_r = wave_r*blah; wave_g = wave_g*blah; wave_b = wave_b*blah;
rot = rot + 0.12*( 0.60*sin(0.21*time) + 0.40*sin(0.339*time) );
cx = cx + 0.30*( 0.60*sin(0.374*time) + 0.14*sin(0.194*time) );
cy = cy + 0.37*( 0.60*sin(0.274*time) + 0.10*sin(0.394*time) );
ib_r = ib_r + 0.2*sin(time*0.5413);
ib_g = ib_g + 0.2*sin(time*0.6459);
ib_b = ib_b + 0.2*sin(time*0.7354);
blah = 0.4/(ib_r+ib_g+ib_b)*3;
ib_r = ib_r*blah; ib_g = ib_g*blah; ib_b = ib_b*blah;

dx = 0.01*cos(time*0.27934+5);
dy = 0.01*sin(time*0.23142+2);

warp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1.x = (1.0 - uv.x);
 tmpvar_1.y = uv.y;
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (max (texture (sampler_main, uv).xyz, texture (sampler_main, tmpvar_1).zyx) * 0.97);
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (((
   (texture (sampler_main, uv).xyz * -2.0)
  + 
   (((texture (sampler_blur1, uv).xyz * scale1) + bias1) * 2.0)
 ) + (
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
  * 2.0)) - 0.7);
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    ret = max(ret, tex2D( sampler_main, float2(1-uv.x,uv.y) ).zyx );

    // darken over time
    ret *= 0.97; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*-2 +
              GetBlur1(uv)*2 +
              GetBlur2(uv)*2 - 0.7;
    //ret *= 1.2;
}
