shapes.frame_eqs_eel:
i=instance;

/// circles

x= .5+ q1*gmegabuf(10000+3*i)/q6;
y= .5+ q1*gmegabuf(10001+3*i);
rad=2*q1*gmegabuf(10002+3*i);

border_g=.5+.5*sin(.2*i);
border_r=.5+.5*sin(.2*i);

//// intersection points

i=instance;   

x= .5+ q1*gmegabuf(3*i)/q6;
y= .5+ q1*gmegabuf(3*i+1);

a=gmegabuf(3*i+2);   a2=a;

rad=rad*q1;

r=.7+.3*sin(1.1*i);  g=.7+.3*sin(1.1*i);  b=1; 
r2=.3*r;             g2=.3*g;              b2=.3*b;

frame_eqs_eel:
warp=0;
wave_a=0;
zoom=1;
decay=0;

q6=aspecty;
q1=.7+.3*sin(.2*time);        //// zoom


///// 2 circles intersection (7x7 times) /////

ind=0;  i1=0;  loop(20, 
xa=.2*sin(-.2*time+.314*i1);    ya=.2*cos(.3*time+.314*i1);                  /// circle A position
ra=.5*q1;                                                                    /// circle A radius
gmegabuf(10000+3*i1)=xa;   gmegabuf(10001+3*i1)=ya;   gmegabuf(10002+3*i1)=ra;

i2=0;  loop(20,
xb=.2*sin(-.2*time+.314*i2);    yb=.2*cos(.3*time+.314*i2);                   /// circle B position
rb=.5*q1;                                                                     /// circle B radius

d=sqrt(sqr(xa-xb)+sqr(ya-yb));     p=(ra*ra-rb*rb+d*d)/(2*d); 
h=sqrt(ra*ra-p*p);                 c=pow(-1,instance);
x=xa+p*(xb-xa)/d;                  y=ya+p*(yb-ya)/d;
x=x-c*h*(yb-ya)/d;                 y=y+c*h*(xb-xa)/d;               /// intersection points
a=if(above(d,ra+rb),0,1);                                           /// hide when one circle is outside other
a=if(below(d,abs(ra-rb)),0,a);                                      /// hide when one circle is inside other
a=if(equal(d,0),0,a);                                               /// hide when circles have the same center


gmegabuf(3*ind)=x;      gmegabuf(3*ind+1)=y;   gmegabuf(3*ind+2)=a;

ind=ind+1;   i2=i2+1;);    i1=i1+1;);
