comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = (texture (sampler_main, uv_1).xyz * 4.0);
 ret_2 = (ret_2 - ((
   (texture (sampler_blur1, uv_1).xyz * scale1)
  + bias1) * 4.0));
 ret_2 = -(ret_2);
 ret_2 = ((pow (ret_2, vec3(0.5, 0.5, 0.7)) - 0.1) * 1.1);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
