}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.8; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    uv = 0.05 + 0.9*uv;

    // equivalent of a laplacian: [center*4 - each of the 4 neighbors*1]
    ret = 0;
    ret += tex2D(sampler_main, uv ).xyz*4;
    ret -= GetBlur1(uv)*4;
ret *= -1;

    // little bit of noise:
    //ret *= 1 + 0.12*(tex2D(sampler_noise_lq, rand_frame.xy 
    //       + uv*texsize.xy*texsize_noise_lq.zw).xxx*2-1);

    // palettize:
    ret = pow(ret, float3(0.5, 0.5, 0.7));

    ret = (ret-0.1)*1.1;

    //ret.xyz *= 2; // a little bit of overbright
}
