warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv2;

    float4 c = float4(1,0.4,0.15,0.1);

    uv2 = uv + float2(1,0)*texsize.zw;
    float gx1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(-1,0)*texsize.zw;
    float gx2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    uv2 = uv + float2(0,1)*texsize.zw;
    float gy1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(0,-1)*texsize.zw;
    float gy2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    float3 g = normalize(float3(gx1-gx2, gy1-gy2, 0.14));

    ret = 0.5 + 0.5*g;
    ret *= saturate(length(ret.xy-0.5)*5);
    ret = ret.xxy;
    ret += 1.15;
    ret *= lerp(ret,ret*(GetBlur3(uv)-GetBlur1(uv)*treb_att),pow(hue_shader.xzy,bass_att));
    ret *= ret;

