waves.point_eqs_eel:
n= sample*6.283;
cubesize=q2;
fix=1/cubesize *0.5;
tm=q1*4+sample*4;

xp= sin(tm)*cos(tm*3)*0.5+0.5;
yp= sin(tm*1.1)*sin(tm*4.1)*0.5+0.5;
zp= sin(tm*2.9)*cos(tm*1.77)*0.5+0.5;

xp= int(xp*cubesize)/cubesize -0.5+fix;
yp= int(yp*cubesize)/cubesize -0.5+fix;
zp= int(zp*cubesize)/cubesize -0.5+fix;


//rotate
ang=t1;
sang=sin(ang);cang=cos(ang);
xq=xp*sang + zp*cang;
yq=yp;
zq=xp*cang - zp*sang;
xp=xq;yp=yq;zp=zq;

//rotate
ang=t1*0.75;
sang=sin(ang);cang=cos(ang);
xq=xp;
yq=yp*sang + zp*cang;
zq=yp*cang - zp*sang;
xp=xq;yp=yq;zp=zq;


zp= zp+2;
x= xp/zp + 0.5;
y= yp/zp * 1.333 + 0.5;
r=bass;
g=mid;
b=treb;

